import React from 'react';  
import './App.css'; 

export default function pop(props) {
	
let text = 'This is a Popup'; 

    return (

<div className='popcontainer'> 

<div className='inner'>  

	<h2>{this.props.text}</h2>  

	<button onClick={this.props.closepop}>Close</button>   

</div>

</div> 
        
    )
}





